"use strict";
var React = require('react')

var TileNumber = React.createClass({

  render() {
    var {number, label} = this.props

    return (
      <div className="tile-number">
        <span className={'tile-number__number tile-number__number--length-'+(number+'').length}>
          {number}
        </span>
        <span className="tile-number__label">{label}</span>
      </div>
    )
  }

})

module.exports = TileNumber
