"use strict";
var React = require('react')
var ChartistGraph = require('react-chartist')

var TileProgress = React.createClass({

  render() {
    var options = {
      showPoint: false,
      low: 0,
      fullWidth: true,
      axisY: {
        showGrid: false,
        showLabel: false
      },
      axisX: {
        showGrid: false,
        showLabel: false
      }
    }

    return (
      <div className="tile-progress">
        <h2 className="tile-progress__label">Performance</h2>
        <ChartistGraph data={this.props.data} options={options} type="Line" />
      </div>
    )
  }

})

module.exports = TileProgress
