"use strict";
var React = require('react')
var _ = require('lodash-node')

var TileWeeks = React.createClass({

  render() {
    var { year, weeks, workoutsPerWeek } = this.props
    return (
      <div className="tile-weeks">
        <div className="tile-weeks__year">
          <button className="tile-weeks__button" onClick={this.props.previousYear}>◀</button>
          {year}
          <button className="tile-weeks__button" onClick={this.props.nextYear}>▶</button>
        </div>
        <div className="tile-weeks__weeks">
          {_.range(0, weeks).map(week =>
            <div className={'tile-weeks__week tile-weeks__week--workouts-'+(workoutsPerWeek[week+1] || 0)}>
              {workoutsPerWeek[week+1] || 0}
            </div>
          )}
          <div className="tile-weeks__label">workouts per week</div>
        </div>
      </div>
    )
  }

})

module.exports = TileWeeks
